import styled from 'styled-components';

const ButtonStyle = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: 'Epilogue';
  font-weight: 700;
  font-size: 20px;
  background: #2FC18C;
  border: none;
  border-radius: 5px;
  width: 100%;
  height: 45px;
  color: #FFFFFF;
  cursor: pointer;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  padding: 12px 16px;
`;

export default ButtonStyle;
